import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Box, Container, Grid, Paper, Typography } from '@mui/material';
import MetricsCard from './components/MetricsCard';
import ChatBot from './components/ChatBot';
import SuggestionsList from './components/SuggestionsList';
import axios from 'axios';

const theme = createTheme({
  palette: {
    primary: {
      main: '#2e7d32',
    },
    secondary: {
      main: '#81c784',
    },
    background: {
      default: '#f5f5f5',
    },
  },
});

function App() {
  const [metrics, setMetrics] = useState(null);
  const [suggestions, setSuggestions] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [metricsRes, suggestionsRes] = await Promise.all([
          axios.get('http://localhost:5000/api/metrics'),
          axios.get('http://localhost:5000/api/suggestions')
        ]);
        setMetrics(metricsRes.data);
        setSuggestions(suggestionsRes.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ flexGrow: 1, minHeight: '100vh', p: 3 }}>
        <Container maxWidth="lg">
          <Typography variant="h4" component="h1" gutterBottom sx={{ mb: 4, color: 'primary.main' }}>
            EcoBot Dashboard
          </Typography>
          
          <Grid container spacing={3}>
            {/* Metrics Section */}
            <Grid item xs={12} md={8}>
              <Paper sx={{ p: 3, height: '100%' }}>
                <Typography variant="h6" gutterBottom>
                  MÃ©tricas de Sustentabilidade
                </Typography>
                <Grid container spacing={2}>
                  {metrics && Object.entries(metrics).map(([key, value]) => (
                    <Grid item xs={12} sm={6} md={4} key={key}>
                      <MetricsCard
                        title={key}
                        current={value.current}
                        previous={value.previous}
                        reduction={value.reduction}
                      />
                    </Grid>
                  ))}
                </Grid>
              </Paper>
            </Grid>

            {/* ChatBot Section */}
            <Grid item xs={12} md={4}>
              <Paper sx={{ p: 3, height: '100%' }}>
                <ChatBot />
              </Paper>
            </Grid>

            {/* Suggestions Section */}
            <Grid item xs={12}>
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom>
                  SugestÃµes de Melhoria
                </Typography>
                <SuggestionsList suggestions={suggestions} />
              </Paper>
            </Grid>
          </Grid>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
