import React from 'react';
import { Card, CardContent, Typography, Box } from '@mui/material';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';

const formatTitle = (title) => {
  return title
    .replace(/([A-Z])/g, ' $1')
    .replace(/^./, (str) => str.toUpperCase());
};

const MetricsCard = ({ title, current, previous, reduction }) => {
  const isImprovement = reduction > 0;
  const formattedTitle = formatTitle(title);

  return (
    <Card sx={{ height: '100%' }}>
      <CardContent>
        <Typography variant="h6" component="div" gutterBottom>
          {formattedTitle}
        </Typography>
        
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Typography variant="h4" component="div" sx={{ mr: 1 }}>
            {current}
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center', color: isImprovement ? 'success.main' : 'error.main' }}>
            {isImprovement ? <TrendingDownIcon /> : <TrendingUpIcon />}
            <Typography variant="body2" sx={{ ml: 0.5 }}>
              {Math.abs(reduction)}%
            </Typography>
          </Box>
        </Box>

        <Typography variant="body2" color="text.secondary">
          Anterior: {previous}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default MetricsCard;
