import React, { useState } from 'react';
import { 
  Box, 
  Grid, 
  Paper, 
  Typography, 
  Tabs,
  Tab,
  Button,
  Chip,
  Divider,
  Select,
  MenuItem,
  FormControl,
  InputLabel
} from '@mui/material';
import TimelineIcon from '@mui/icons-material/Timeline';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import DownloadIcon from '@mui/icons-material/Download';
import FilterListIcon from '@mui/icons-material/FilterList';
import MetricsCard from '../components/MetricsCard';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend, 
  ResponsiveContainer,
  LineChart,
  Line,
  PieChart,
  Pie,
  Cell
} from 'recharts';

const Metrics = ({ mockData }) => {
  const [timeRange, setTimeRange] = useState('month');
  const [selectedTab, setSelectedTab] = useState(0);

  const handleTimeRangeChange = (event) => {
    setTimeRange(event.target.value);
  };

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  // Cores para os gráficos
  const COLORS = ['#1B3B36', '#4CAF50', '#2e7d32', '#ed6c02', '#9c27b0'];

  // Dados para o gráfico de barras
  const barChartData = [
    { name: 'Jan', consumo: 1800, meta: 1600 },
    { name: 'Fev', consumo: 1750, meta: 1600 },
    { name: 'Mar', consumo: 1700, meta: 1600 },
    { name: 'Abr', consumo: 1650, meta: 1600 },
    { name: 'Mai', consumo: 1600, meta: 1600 },
    { name: 'Jun', consumo: 1500, meta: 1600 },
  ];

  // Dados para o gráfico de linha
  const lineChartData = [
    { name: 'Jan', emissões: 250, meta: 230 },
    { name: 'Fev', emissões: 240, meta: 230 },
    { name: 'Mar', emissões: 230, meta: 230 },
    { name: 'Abr', emissões: 220, meta: 230 },
    { name: 'Mai', emissões: 210, meta: 230 },
    { name: 'Jun', emissões: 200, meta: 230 },
  ];

  // Dados para o gráfico de pizza
  const pieChartData = [
    { name: 'Servidores', value: 40 },
    { name: 'Ar-condicionado', value: 30 },
    { name: 'Iluminação', value: 20 },
    { name: 'Outros', value: 10 },
  ];

  return (
    <Box>
      {/* Cabeçalho */}
      <Paper sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center' }}>
            <TimelineIcon sx={{ mr: 1 }} />
            Métricas de Sustentabilidade
          </Typography>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <FormControl size="small" sx={{ minWidth: 120 }}>
              <InputLabel id="time-range-label">Período</InputLabel>
              <Select
                labelId="time-range-label"
                value={timeRange}
                label="Período"
                onChange={handleTimeRangeChange}
              >
                <MenuItem value="week">Última semana</MenuItem>
                <MenuItem value="month">Último mês</MenuItem>
                <MenuItem value="quarter">Último trimestre</MenuItem>
                <MenuItem value="year">Último ano</MenuItem>
              </Select>
            </FormControl>
            <Button 
              variant="outlined" 
              startIcon={<FilterListIcon />}
            >
              Filtros
            </Button>
            <Button 
              variant="contained" 
              startIcon={<DownloadIcon />}
            >
              Exportar
            </Button>
          </Box>
        </Box>

        <Tabs 
          value={selectedTab} 
          onChange={handleTabChange} 
          sx={{ borderBottom: 1, borderColor: 'divider', mb: 3 }}
        >
          <Tab label="Visão Geral" />
          <Tab label="Consumo de Energia" />
          <Tab label="Emissões de CO2" />
          <Tab label="Armazenamento Digital" />
          <Tab label="Eficiência" />
        </Tabs>

        {/* Métricas Principais */}
        <Grid container spacing={3} sx={{ mb: 3 }}>
          {Object.entries(mockData.metrics).map(([key, value]) => (
            <Grid item xs={12} sm={6} md={3} key={key}>
              <MetricsCard
                title={key}
                value={value.current}
                unit={value.unit}
                reduction={value.reduction}
                previous={value.previous}
                impact={value.impact}
                icon={value.icon}
                color={value.color}
                data={value.data}
              />
            </Grid>
          ))}
        </Grid>
      </Paper>

      {/* Gráficos */}
      <Grid container spacing={3}>
        {/* Gráfico de Barras - Consumo de Energia */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Consumo de Energia vs. Meta
            </Typography>
            <Box sx={{ height: 300 }}>
              <ResponsiveContainer width="100%" height="100%">
                <BarChart
                  data={barChartData}
                  margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Bar dataKey="consumo" name="Consumo (kWh)" fill="#1B3B36" />
                  <Bar dataKey="meta" name="Meta (kWh)" fill="#4CAF50" />
                </BarChart>
              </ResponsiveContainer>
            </Box>
          </Paper>
        </Grid>

        {/* Gráfico de Pizza - Distribuição de Consumo */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Typography variant="h6" gutterBottom>
              Distribuição de Consumo
            </Typography>
            <Box sx={{ height: 300 }}>
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={pieChartData}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                    label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                  >
                    {pieChartData.map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                  <Legend />
                </PieChart>
              </ResponsiveContainer>
            </Box>
          </Paper>
        </Grid>

        {/* Gráfico de Linha - Emissões de CO2 */}
        <Grid item xs={12}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Emissões de CO2 ao Longo do Tempo
            </Typography>
            <Box sx={{ height: 300 }}>
              <ResponsiveContainer width="100%" height="100%">
                <LineChart
                  data={lineChartData}
                  margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line 
                    type="monotone" 
                    dataKey="emissões" 
                    name="Emissões (tCO2)" 
                    stroke="#2e7d32" 
                    activeDot={{ r: 8 }} 
                  />
                  <Line 
                    type="monotone" 
                    dataKey="meta" 
                    name="Meta (tCO2)" 
                    stroke="#ed6c02" 
                    strokeDasharray="5 5" 
                  />
                </LineChart>
              </ResponsiveContainer>
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Metrics;
