import React, { useState } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { 
  Box, 
  Container, 
  Grid, 
  Paper, 
  Typography, 
  Card, 
  CardContent,
  Avatar,
  Divider,
  LinearProgress,
  Chip,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Button,
  Drawer,
  IconButton,
  useTheme,
  useMediaQuery,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import DashboardIcon from '@mui/icons-material/Dashboard';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import WarningIcon from '@mui/icons-material/Warning';
import StorageIcon from '@mui/icons-material/Storage';
import CloudIcon from '@mui/icons-material/Cloud';
import SpeedIcon from '@mui/icons-material/Speed';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import EmailIcon from '@mui/icons-material/Email';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import GroupsIcon from '@mui/icons-material/Groups';
import AssessmentIcon from '@mui/icons-material/Assessment';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import InfoIcon from '@mui/icons-material/Info';
import TimelineIcon from '@mui/icons-material/Timeline';
import LocalOfferIcon from '@mui/icons-material/LocalOffer';
import ChatIcon from '@mui/icons-material/Chat';
import IntegrationInstructionsIcon from '@mui/icons-material/IntegrationInstructions';
import SettingsIcon from '@mui/icons-material/Settings';
import PersonIcon from '@mui/icons-material/Person';
import LogoutIcon from '@mui/icons-material/Logout';
import MetricsCard from './components/MetricsCard';

// Importando as páginas
import Dashboard from './pages/Dashboard';
import Metrics from './pages/Metrics';
import Suggestions from './pages/Suggestions';

// Dados mockados expandidos
const mockData = {
  metrics: {
    energyConsumption: {
      current: 1500,
      previous: 1800,
      reduction: 16.7,
      unit: 'kWh',
      impact: 'Redução significativa no consumo',
      icon: <SpeedIcon />,
      color: '#1B3B36',
      data: [
        { name: 'Jan', value: 1800 },
        { name: 'Fev', value: 1750 },
        { name: 'Mar', value: 1700 },
        { name: 'Abr', value: 1650 },
        { name: 'Mai', value: 1600 },
        { name: 'Jun', value: 1500 },
      ]
    },
    digitalStorage: {
      current: 500,
      previous: 700,
      reduction: 28.6,
      unit: 'GB',
      impact: 'Otimização de armazenamento',
      icon: <StorageIcon />,
      color: '#4CAF50',
      data: [
        { name: 'Jan', value: 700 },
        { name: 'Fev', value: 650 },
        { name: 'Mar', value: 600 },
        { name: 'Abr', value: 550 },
        { name: 'Mai', value: 520 },
        { name: 'Jun', value: 500 },
      ]
    },
    carbonEmissions: {
      current: 200,
      previous: 250,
      reduction: 20,
      unit: 'tCO2',
      impact: 'Redução de emissões',
      icon: <CloudIcon />,
      color: '#2e7d32',
      data: [
        { name: 'Jan', value: 250 },
        { name: 'Fev', value: 240 },
        { name: 'Mar', value: 230 },
        { name: 'Abr', value: 220 },
        { name: 'Mai', value: 210 },
        { name: 'Jun', value: 200 },
      ]
    },
    efficiency: {
      current: 85,
      previous: 75,
      reduction: -13.3,
      unit: '%',
      impact: 'Aumento na eficiência',
      icon: <CheckCircleIcon />,
      color: '#ed6c02',
      data: [
        { name: 'Jan', value: 75 },
        { name: 'Fev', value: 77 },
        { name: 'Mar', value: 79 },
        { name: 'Abr', value: 81 },
        { name: 'Mai', value: 83 },
        { name: 'Jun', value: 85 },
      ]
    },
    impactoEmail: {
      current: 2500,
      previous: 3000,
      reduction: 16.7,
      unit: "e-mails",
      icon: <EmailIcon />,
      color: "#9c27b0",
      impact: "125kg CO2 evitados"
    }
  },
  integrations: {
    active: [
      { name: "Slack", status: "Ativo", icon: <ChatIcon /> },
      { name: "Microsoft Teams", status: "Ativo", icon: <GroupsIcon /> },
      { name: "Google Workspace", status: "Ativo", icon: <EmailIcon /> }
    ],
    available: [
      { name: "AWS", icon: <CloudIcon /> },
      { name: "Azure", icon: <CloudIcon /> },
      { name: "Google Cloud", icon: <CloudIcon /> }
    ]
  },
  gamification: {
    topDepartments: [
      {
        name: "TI",
        score: 850,
        reduction: "25%",
        members: 15,
        achievements: ["Mestre da Economia", "Líder Verde"]
      },
      {
        name: "Marketing",
        score: 720,
        reduction: "20%",
        members: 8,
        achievements: ["Economista Digital"]
      },
      {
        name: "RH",
        score: 680,
        reduction: "18%",
        members: 5,
        achievements: ["Iniciante Verde"]
      }
    ],
    userAchievements: [
      {
        title: "Mestre da Economia",
        description: "Reduziu consumo em 30%",
        icon: <EmojiEventsIcon />,
        date: "15/03/2024"
      },
      {
        title: "Líder Verde",
        description: "Liderou equipe em práticas sustentáveis",
        icon: <CheckCircleIcon />,
        date: "10/03/2024"
      }
    ]
  },
  alerts: [
    {
      type: "warning",
      message: "E-mail com anexo grande detectado (8MB)",
      suggestion: "Compacte o arquivo ou use link de compartilhamento",
      timestamp: "há 5 minutos"
    },
    {
      type: "info",
      message: "Backup redundante identificado",
      suggestion: "Considere excluir versões antigas",
      timestamp: "há 15 minutos"
    }
  ],
  suggestions: [
    {
      id: 1,
      title: "Otimizar Armazenamento em Nuvem",
      description: "Identificamos 200GB de arquivos duplicados que podem ser removidos",
      impact: "Redução de 15% no consumo de energia",
      priority: "high",
      progress: 75,
      status: "Em andamento",
      department: "TI",
      deadline: "30/03/2024"
    },
    {
      id: 2,
      title: "Ajustar Configurações de Servidores",
      description: "Configurar hibernação automática em períodos de baixa atividade",
      impact: "Redução de 10% no consumo de energia",
      priority: "medium",
      progress: 30,
      status: "Planejado",
      department: "Infraestrutura",
      deadline: "15/04/2024"
    },
    {
      id: 3,
      title: "Implementar Virtualização",
      description: "Consolidar servidores físicos em máquinas virtuais",
      impact: "Redução de 25% no consumo de energia",
      priority: "high",
      progress: 10,
      status: "Planejado",
      department: "TI",
      deadline: "30/04/2024"
    }
  ],
  esgMetrics: {
    environmental: {
      score: 89,
      target: 90,
      status: "Em progresso"
    },
    social: {
      score: 92,
      target: 90,
      status: "Atingido"
    },
    governance: {
      score: 85,
      target: 90,
      status: "Em progresso"
    }
  }
};

const theme = createTheme({
  palette: {
    primary: {
      main: '#1B3B36',
      light: '#2e7d32',
      dark: '#0a1f1c',
      contrastText: '#ffffff',
    },
    secondary: {
      main: '#4CAF50',
      light: '#81c784',
      dark: '#388e3c',
      contrastText: '#ffffff',
    },
    background: {
      default: '#F5F7FA',
      paper: '#ffffff',
    },
    success: {
      main: '#2e7d32',
      light: '#4caf50',
      dark: '#1b5e20',
    },
    warning: {
      main: '#ed6c02',
      light: '#ff9800',
      dark: '#e65100',
    },
    error: {
      main: '#d32f2f',
      light: '#ef5350',
      dark: '#c62828',
    },
  },
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 700,
      letterSpacing: '-0.5px',
    },
    h6: {
      fontWeight: 600,
      letterSpacing: '-0.3px',
    },
    subtitle1: {
      fontWeight: 500,
      letterSpacing: '0.15px',
    },
    body1: {
      letterSpacing: '0.15px',
    },
  },
  shape: {
    borderRadius: 12,
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.05)',
          transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
          '&:hover': {
            transform: 'translateY(-4px)',
            boxShadow: '0 8px 25px rgba(0, 0, 0, 0.1)',
          },
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          fontWeight: 600,
          padding: '8px 24px',
          borderRadius: '8px',
        },
        contained: {
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
          '&:hover': {
            boxShadow: '0 6px 16px rgba(0, 0, 0, 0.15)',
          },
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.05)',
        },
      },
    },
    MuiLinearProgress: {
      styleOverrides: {
        root: {
          borderRadius: 4,
          height: 8,
        },
      },
    },
  },
});

function App() {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [selectedMenuItem, setSelectedMenuItem] = useState('Dashboard');
  const [showESGReport, setShowESGReport] = useState(false);
  const [showIntegrations, setShowIntegrations] = useState(false);
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));

  const handleMenuItemClick = (text) => {
    setSelectedMenuItem(text);
    setDrawerOpen(false);
  };

  const handleESGReport = () => {
    setShowESGReport(true);
  };

  const handleIntegrations = () => {
    setShowIntegrations(true);
  };

  const handleCloseESGReport = () => {
    setShowESGReport(false);
  };

  const handleCloseIntegrations = () => {
    setShowIntegrations(false);
  };

  const menuItems = [
    { text: 'Dashboard', icon: <DashboardIcon /> },
    { text: 'Métricas', icon: <TimelineIcon /> },
    { text: 'Sugestões', icon: <LocalOfferIcon /> },
    { text: 'ESG', icon: <AssessmentIcon /> },
    { text: 'Chat', icon: <ChatIcon /> },
    { text: 'Integrações', icon: <IntegrationInstructionsIcon /> },
    { text: 'Configurações', icon: <SettingsIcon /> },
  ];

  const renderContent = () => {
    switch (selectedMenuItem) {
      case 'Dashboard':
        return <Dashboard mockData={mockData} />;
      case 'Métricas':
        return <Metrics mockData={mockData} />;
      case 'Sugestões':
        return <Suggestions mockData={mockData} />;
      default:
        return <Dashboard mockData={mockData} />;
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ display: 'flex', minHeight: '100vh' }}>
        {/* Sidebar */}
        <Drawer
          variant={isMobile ? 'temporary' : 'permanent'}
          open={drawerOpen}
          onClose={() => setDrawerOpen(false)}
          sx={{
            width: 240,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: 240,
              boxSizing: 'border-box',
              bgcolor: 'background.paper',
              borderRight: '1px solid',
              borderColor: 'divider',
            },
          }}
        >
          <Box sx={{ 
            p: 2, 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between',
            borderBottom: '1px solid',
            borderColor: 'divider'
          }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Avatar 
                src="/ecobot-logo.png" 
                sx={{ 
                  width: 40, 
                  height: 40, 
                  mr: 1,
                  border: '2px solid',
                  borderColor: 'primary.main'
                }}
              />
              <Typography variant="h6" color="primary.main" sx={{ fontWeight: 600 }}>
                EcoBot
              </Typography>
            </Box>
            {isMobile && (
              <IconButton onClick={() => setDrawerOpen(false)}>
                <ChevronLeftIcon />
              </IconButton>
            )}
          </Box>

          <List sx={{ p: 2 }}>
            {menuItems.map((item) => (
              <ListItem
                button
                key={item.text}
                onClick={() => handleMenuItemClick(item.text)}
                sx={{
                  mb: 1,
                  borderRadius: 1,
                  bgcolor: selectedMenuItem === item.text ? 'primary.light' : 'transparent',
                  '&:hover': {
                    bgcolor: 'primary.light',
                    '& .MuiListItemIcon-root': {
                      color: 'primary.main',
                    },
                  },
                }}
              >
                <ListItemIcon sx={{ minWidth: 40 }}>
                  {item.icon}
                </ListItemIcon>
                <ListItemText primary={item.text} />
              </ListItem>
            ))}
          </List>

          <Box sx={{ mt: 'auto', p: 2, borderTop: '1px solid', borderColor: 'divider' }}>
            <ListItem
              button
              onClick={() => console.log('Logout...')}
              sx={{
                borderRadius: 1,
                '&:hover': {
                  bgcolor: 'error.light',
                  '& .MuiListItemIcon-root': {
                    color: 'error.main',
                  },
                },
              }}
            >
              <ListItemIcon sx={{ minWidth: 40, color: 'error.main' }}>
                <LogoutIcon />
              </ListItemIcon>
              <ListItemText primary="Sair" />
            </ListItem>
          </Box>
        </Drawer>

        {/* Main Content */}
        <Box sx={{ flexGrow: 1, p: 3 }}>
          {/* Header */}
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'space-between', 
            mb: 4,
            p: 3,
            bgcolor: 'background.paper',
            borderRadius: 2,
            boxShadow: '0 4px 20px rgba(0, 0, 0, 0.05)',
          }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              {isMobile && (
                <IconButton 
                  onClick={() => setDrawerOpen(true)}
                  sx={{ mr: 2 }}
                >
                  <MenuIcon />
                </IconButton>
              )}
              <Box>
                <Typography variant="h4" component="h1" color="primary.main" sx={{ mb: 0.5 }}>
                  {selectedMenuItem}
                </Typography>
                <Typography variant="subtitle1" color="text.secondary">
                  Bem-vindo ao EcoBot
                </Typography>
              </Box>
            </Box>
            <Box sx={{ display: 'flex', gap: 2 }}>
              <Button 
                variant="contained" 
                color="primary"
                startIcon={<AssessmentIcon />}
                onClick={handleESGReport}
                sx={{
                  background: 'linear-gradient(45deg, #1B3B36 30%, #2e7d32 90%)',
                }}
              >
                Relatório ESG
              </Button>
              <Button 
                variant="outlined" 
                color="primary"
                startIcon={<IntegrationInstructionsIcon />}
                onClick={handleIntegrations}
                sx={{
                  borderWidth: 2,
                  '&:hover': {
                    borderWidth: 2,
                  },
                }}
              >
                Integrações
              </Button>
            </Box>
          </Box>

          {/* Page Content */}
          {renderContent()}

          {/* ESG Report Dialog */}
          <Dialog
            open={showESGReport}
            onClose={handleCloseESGReport}
            maxWidth="md"
            fullWidth
          >
            <DialogTitle>
              <Typography variant="h5" component="div" sx={{ display: 'flex', alignItems: 'center' }}>
                <AssessmentIcon sx={{ mr: 1 }} />
                Relatório ESG
              </Typography>
            </DialogTitle>
            <DialogContent>
              <TableContainer component={Paper} sx={{ mt: 2 }}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Métrica</TableCell>
                      <TableCell align="right">Score Atual</TableCell>
                      <TableCell align="right">Meta</TableCell>
                      <TableCell align="right">Status</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {Object.entries(mockData.esgMetrics).map(([key, value]) => (
                      <TableRow key={key}>
                        <TableCell component="th" scope="row">
                          {key.charAt(0).toUpperCase() + key.slice(1)}
                        </TableCell>
                        <TableCell align="right">{value.score}</TableCell>
                        <TableCell align="right">{value.target}</TableCell>
                        <TableCell align="right">
                          <Chip 
                            label={value.status}
                            color={value.status === "Atingido" ? "success" : "warning"}
                            size="small"
                          />
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleCloseESGReport}>Fechar</Button>
              <Button variant="contained" color="primary">
                Exportar PDF
              </Button>
            </DialogActions>
          </Dialog>

          {/* Integrations Dialog */}
          <Dialog
            open={showIntegrations}
            onClose={handleCloseIntegrations}
            maxWidth="md"
            fullWidth
          >
            <DialogTitle>
              <Typography variant="h5" component="div" sx={{ display: 'flex', alignItems: 'center' }}>
                <IntegrationInstructionsIcon sx={{ mr: 1 }} />
                Gerenciar Integrações
              </Typography>
            </DialogTitle>
            <DialogContent>
              <Grid container spacing={3} sx={{ mt: 1 }}>
                <Grid item xs={12}>
                  <Typography variant="h6" gutterBottom>
                    Integrações Ativas
                  </Typography>
                  <List>
                    {mockData.integrations.active.map((integration, index) => (
                      <ListItem key={index}>
                        <ListItemIcon>
                          {integration.icon}
                        </ListItemIcon>
                        <ListItemText
                          primary={integration.name}
                          secondary={integration.status}
                        />
                        <Chip 
                          label="Ativo" 
                          color="success" 
                          size="small"
                        />
                      </ListItem>
                    ))}
                  </List>
                </Grid>
                <Grid item xs={12}>
                  <Typography variant="h6" gutterBottom>
                    Integrações Disponíveis
                  </Typography>
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                    {mockData.integrations.available.map((integration, index) => (
                      <Chip
                        key={index}
                        icon={integration.icon}
                        label={integration.name}
                        variant="outlined"
                        sx={{ m: 0.5 }}
                        onClick={() => console.log('Integrar com:', integration.name)}
                      />
                    ))}
                  </Box>
                </Grid>
              </Grid>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleCloseIntegrations}>Fechar</Button>
              <Button variant="contained" color="primary">
                Adicionar Nova Integração
              </Button>
            </DialogActions>
          </Dialog>
        </Box>
      </Box>
    </ThemeProvider>
  );
}

export default App;
