import React from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { 
  Box, 
  Container, 
  Grid, 
  Paper, 
  Typography, 
  Card, 
  CardContent,
  Avatar,
  Divider,
  LinearProgress,
  Chip,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Button,
  IconButton,
  Tooltip
} from '@mui/material';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import WarningIcon from '@mui/icons-material/Warning';
import StorageIcon from '@mui/icons-material/Storage';
import CloudIcon from '@mui/icons-material/Cloud';
import SpeedIcon from '@mui/icons-material/Speed';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import EmailIcon from '@mui/icons-material/Email';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import GroupsIcon from '@mui/icons-material/Groups';
import AssessmentIcon from '@mui/icons-material/Assessment';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import InfoIcon from '@mui/icons-material/Info';
import TimelineIcon from '@mui/icons-material/Timeline';
import LocalOfferIcon from '@mui/icons-material/LocalOffer';
import ChatIcon from '@mui/icons-material/Chat';
import IntegrationInstructionsIcon from '@mui/icons-material/IntegrationInstructions';

// Dados mockados expandidos
const mockData = {
  metrics: {
    energyConsumption: {
      current: 1500,
      previous: 1800,
      reduction: 16.7,
      unit: "kWh",
      icon: <SpeedIcon />,
      color: "#4caf50",
      impact: "Equivalente a 12 árvores"
    },
    digitalStorage: {
      current: 500,
      previous: 700,
      reduction: 28.6,
      unit: "GB",
      icon: <StorageIcon />,
      color: "#2196f3",
      impact: "Economia de R$ 1.200/ano"
    },
    carbonEmissions: {
      current: 200,
      previous: 250,
      reduction: 20,
      unit: "kg CO2",
      icon: <CloudIcon />,
      color: "#f44336",
      impact: "Redução de 20% nas emissões"
    },
    emailImpact: {
      current: 2500,
      previous: 3000,
      reduction: 16.7,
      unit: "e-mails",
      icon: <EmailIcon />,
      color: "#9c27b0",
      impact: "125kg CO2 evitados"
    }
  },
  integrations: {
    active: [
      { name: "Slack", status: "Ativo", icon: <ChatIcon /> },
      { name: "Microsoft Teams", status: "Ativo", icon: <GroupsIcon /> },
      { name: "Google Workspace", status: "Ativo", icon: <EmailIcon /> }
    ],
    available: [
      { name: "AWS", icon: <CloudIcon /> },
      { name: "Azure", icon: <CloudIcon /> },
      { name: "Google Cloud", icon: <CloudIcon /> }
    ]
  },
  gamification: {
    topDepartments: [
      {
        name: "TI",
        score: 850,
        reduction: "25%",
        members: 15,
        achievements: ["Mestre da Economia", "Líder Verde"]
      },
      {
        name: "Marketing",
        score: 720,
        reduction: "20%",
        members: 8,
        achievements: ["Economista Digital"]
      },
      {
        name: "RH",
        score: 680,
        reduction: "18%",
        members: 5,
        achievements: ["Iniciante Verde"]
      }
    ],
    userAchievements: [
      {
        title: "Mestre da Economia",
        description: "Reduziu consumo em 30%",
        icon: <EmojiEventsIcon />,
        date: "15/03/2024"
      },
      {
        title: "Líder Verde",
        description: "Liderou equipe em práticas sustentáveis",
        icon: <CheckCircleIcon />,
        date: "10/03/2024"
      }
    ]
  },
  alerts: [
    {
      type: "warning",
      message: "E-mail com anexo grande detectado (8MB)",
      suggestion: "Compacte o arquivo ou use link de compartilhamento",
      timestamp: "há 5 minutos"
    },
    {
      type: "info",
      message: "Backup redundante identificado",
      suggestion: "Considere excluir versões antigas",
      timestamp: "há 15 minutos"
    }
  ],
  suggestions: [
    {
      id: 1,
      title: "Otimizar Armazenamento em Nuvem",
      description: "Identificamos 200GB de arquivos duplicados que podem ser removidos",
      impact: "Redução de 15% no consumo de energia",
      priority: "high",
      progress: 75,
      status: "Em andamento",
      department: "TI",
      deadline: "30/03/2024"
    },
    {
      id: 2,
      title: "Ajustar Configurações de Servidores",
      description: "Configurar hibernação automática em períodos de baixa atividade",
      impact: "Redução de 10% no consumo de energia",
      priority: "medium",
      progress: 30,
      status: "Planejado",
      department: "Infraestrutura",
      deadline: "15/04/2024"
    },
    {
      id: 3,
      title: "Implementar Virtualização",
      description: "Consolidar servidores físicos em máquinas virtuais",
      impact: "Redução de 25% no consumo de energia",
      priority: "high",
      progress: 10,
      status: "Planejado",
      department: "TI",
      deadline: "30/04/2024"
    }
  ],
  esgMetrics: {
    environmental: {
      score: 89,
      target: 90,
      status: "Em progresso"
    },
    social: {
      score: 92,
      target: 90,
      status: "Atingido"
    },
    governance: {
      score: 85,
      target: 90,
      status: "Em progresso"
    }
  }
};

const theme = createTheme({
  palette: {
    primary: {
      main: '#1B3B36',
      light: '#2e7d32',
    },
    secondary: {
      main: '#4CAF50',
      light: '#81c784',
    },
    background: {
      default: '#FAF6F1',
      paper: '#ffffff',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
    h4: {
      fontWeight: 600,
    },
    h6: {
      fontWeight: 500,
    },
  },
});

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ flexGrow: 1, minHeight: '100vh', p: 3, bgcolor: 'background.default' }}>
        <Container maxWidth="xl">
          {/* Header */}
          <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mb: 4 }}>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Avatar 
                src="/ecobot-logo.png" 
                sx={{ 
                  width: 60, 
                  height: 60, 
                  mr: 2,
                  border: '2px solid',
                  borderColor: 'primary.main'
                }}
              />
              <Box>
                <Typography variant="h4" component="h1" color="primary.main">
                  EcoBot Dashboard
                </Typography>
                <Typography variant="subtitle1" color="text.secondary">
                  Monitoramento de Sustentabilidade e ESG
                </Typography>
              </Box>
            </Box>
            <Box sx={{ display: 'flex', gap: 2 }}>
              <Button 
                variant="contained" 
                color="primary"
                startIcon={<AssessmentIcon />}
              >
                Relatório ESG
              </Button>
              <Button 
                variant="outlined" 
                color="primary"
                startIcon={<IntegrationInstructionsIcon />}
              >
                Integrações
              </Button>
            </Box>
          </Box>
          
          <Grid container spacing={3}>
            {/* Métricas Principais */}
            <Grid item xs={12}>
              <Paper sx={{ p: 3 }}>
                <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                  <TimelineIcon sx={{ mr: 1 }} />
                  Métricas de Sustentabilidade
                </Typography>
                <Grid container spacing={2}>
                  {Object.entries(mockData.metrics).map(([key, value]) => (
                    <Grid item xs={12} sm={6} md={3} key={key}>
                      <Card sx={{ height: '100%' }}>
                        <CardContent>
                          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                            <Avatar sx={{ bgcolor: value.color, mr: 2 }}>
                              {value.icon}
                            </Avatar>
                            <Typography variant="h6" component="div">
                              {key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
                            </Typography>
                          </Box>
                          <Box sx={{ display: 'flex', alignItems: 'baseline', mb: 1 }}>
                            <Typography variant="h4" component="div" sx={{ mr: 1 }}>
                              {value.current}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              {value.unit}
                            </Typography>
                          </Box>
                          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                            <Box sx={{ display: 'flex', alignItems: 'center', color: value.reduction > 0 ? 'success.main' : 'error.main' }}>
                              {value.reduction > 0 ? <TrendingDownIcon /> : <TrendingUpIcon />}
                              <Typography variant="body2" sx={{ ml: 0.5 }}>
                                {Math.abs(value.reduction)}%
                              </Typography>
                            </Box>
                            <Typography variant="body2" color="text.secondary" sx={{ ml: 2 }}>
                              vs. anterior: {value.previous} {value.unit}
                            </Typography>
                          </Box>
                          <Typography variant="body2" color="success.main">
                            {value.impact}
                          </Typography>
                          <LinearProgress 
                            variant="determinate" 
                            value={value.reduction > 0 ? 100 : 0} 
                            color={value.reduction > 0 ? "success" : "error"}
                            sx={{ height: 8, borderRadius: 4, mt: 1 }}
                          />
                        </CardContent>
                      </Card>
                    </Grid>
                  ))}
                </Grid>
              </Paper>
            </Grid>

            {/* ChatBot e Alertas */}
            <Grid item xs={12} md={4}>
              <Grid container spacing={3}>
                {/* ChatBot */}
                <Grid item xs={12}>
                  <Paper sx={{ p: 3 }}>
                    <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                      <ChatIcon sx={{ mr: 1 }} />
                      EcoBot Assistant
                    </Typography>
                    <Box sx={{ height: 200, overflow: 'auto', bgcolor: 'background.default', p: 2, borderRadius: 1, mb: 2 }}>
                      <Typography variant="body1" color="primary" sx={{ display: 'flex', alignItems: 'center' }}>
                        <Avatar src="/ecobot-logo.png" sx={{ width: 24, height: 24, mr: 1 }} />
                        Olá! Sou o EcoBot, seu assistente de sustentabilidade!
                      </Typography>
                      <Typography variant="body2" sx={{ mt: 2, color: 'text.secondary' }}>
                        Posso ajudar com:
                      </Typography>
                      <Box sx={{ mt: 1 }}>
                        <Chip 
                          icon={<EmailIcon />} 
                          label="Análise de e-mails" 
                          size="small" 
                          sx={{ mr: 1, mb: 1 }} 
                        />
                        <Chip 
                          icon={<CloudIcon />} 
                          label="Otimização de nuvem" 
                          size="small" 
                          sx={{ mr: 1, mb: 1 }} 
                        />
                        <Chip 
                          icon={<AssessmentIcon />} 
                          label="Relatórios ESG" 
                          size="small" 
                          sx={{ mb: 1 }} 
                        />
                      </Box>
                      <Typography variant="body2" color="success.main" sx={{ mt: 2 }}>
                        Dica do dia: Compactar anexos acima de 5MB pode reduzir seu impacto em 50%!
                      </Typography>
                    </Box>
                    <Divider sx={{ my: 2 }} />
                    <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                      <Button variant="outlined" size="small" startIcon={<IntegrationInstructionsIcon />}>
                        Integrar
                      </Button>
                      <Button variant="contained" size="small" startIcon={<ChatIcon />}>
                        Conversar
                      </Button>
                    </Box>
                  </Paper>
                </Grid>

                {/* Alertas */}
                <Grid item xs={12}>
                  <Paper sx={{ p: 3 }}>
                    <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                      <NotificationsActiveIcon sx={{ mr: 1 }} />
                      Alertas Recentes
                    </Typography>
                    <List>
                      {mockData.alerts.map((alert, index) => (
                        <ListItem key={index} sx={{ 
                          bgcolor: alert.type === 'warning' ? 'warning.light' : 'info.light',
                          mb: 1,
                          borderRadius: 1
                        }}>
                          <ListItemIcon>
                            {alert.type === 'warning' ? <WarningIcon color="warning" /> : <InfoIcon color="info" />}
                          </ListItemIcon>
                          <ListItemText
                            primary={alert.message}
                            secondary={
                              <>
                                <Typography variant="body2">{alert.suggestion}</Typography>
                                <Typography variant="caption" color="text.secondary">
                                  {alert.timestamp}
                                </Typography>
                              </>
                            }
                          />
                        </ListItem>
                      ))}
                    </List>
                  </Paper>
                </Grid>
              </Grid>
            </Grid>

            {/* Gamificação e Integrações */}
            <Grid item xs={12} md={8}>
              <Grid container spacing={3}>
                {/* Gamificação */}
                <Grid item xs={12}>
                  <Paper sx={{ p: 3 }}>
                    <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                      <EmojiEventsIcon sx={{ mr: 1 }} />
                      Ranking de Departamentos
                    </Typography>
                    <Grid container spacing={2}>
                      {mockData.gamification.topDepartments.map((dept, index) => (
                        <Grid item xs={12} md={4} key={index}>
                          <Paper sx={{ 
                            p: 2,
                            bgcolor: index === 0 ? 'rgba(255, 223, 0, 0.1)' : 'transparent',
                            border: index === 0 ? 2 : 1,
                            borderColor: index === 0 ? 'warning.main' : 'divider'
                          }}>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <Avatar sx={{ 
                                bgcolor: index === 0 ? 'warning.main' : 'primary.main',
                                mr: 2
                              }}>
                                {index + 1}
                              </Avatar>
                              <Box>
                                <Typography variant="h6">{dept.name}</Typography>
                                <Typography variant="body2" color="text.secondary">
                                  {dept.members} membros
                                </Typography>
                              </Box>
                            </Box>
                            <Box sx={{ mt: 1 }}>
                              <Typography variant="body2">
                                Score: {dept.score}
                              </Typography>
                              <Typography variant="body2" color="success.main">
                                Redução: {dept.reduction}
                              </Typography>
                              <Box sx={{ mt: 1 }}>
                                {dept.achievements.map((achievement, i) => (
                                  <Chip
                                    key={i}
                                    label={achievement}
                                    size="small"
                                    sx={{ mr: 0.5, mb: 0.5 }}
                                  />
                                ))}
                              </Box>
                            </Box>
                          </Paper>
                        </Grid>
                      ))}
                    </Grid>
                  </Paper>
                </Grid>

                {/* Integrações */}
                <Grid item xs={12}>
                  <Paper sx={{ p: 3 }}>
                    <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                      <IntegrationInstructionsIcon sx={{ mr: 1 }} />
                      Integrações
                    </Typography>
                    <Grid container spacing={2}>
                      <Grid item xs={12} md={6}>
                        <Typography variant="subtitle1" gutterBottom>
                          Integrações Ativas
                        </Typography>
                        <List>
                          {mockData.integrations.active.map((integration, index) => (
                            <ListItem key={index}>
                              <ListItemIcon>
                                {integration.icon}
                              </ListItemIcon>
                              <ListItemText
                                primary={integration.name}
                                secondary={integration.status}
                              />
                              <Chip 
                                label="Ativo" 
                                color="success" 
                                size="small"
                              />
                            </ListItem>
                          ))}
                        </List>
                      </Grid>
                      <Grid item xs={12} md={6}>
                        <Typography variant="subtitle1" gutterBottom>
                          Integrações Disponíveis
                        </Typography>
                        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                          {mockData.integrations.available.map((integration, index) => (
                            <Chip
                              key={index}
                              icon={integration.icon}
                              label={integration.name}
                              variant="outlined"
                              sx={{ m: 0.5 }}
                            />
                          ))}
                        </Box>
                      </Grid>
                    </Grid>
                  </Paper>
                </Grid>
              </Grid>
            </Grid>

            {/* Sugestões e ESG */}
            <Grid item xs={12}>
              <Grid container spacing={3}>
                {/* Sugestões */}
                <Grid item xs={12} md={8}>
                  <Paper sx={{ p: 3 }}>
                    <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                      <LocalOfferIcon sx={{ mr: 1 }} />
                      Sugestões de Melhoria
                    </Typography>
                    <Grid container spacing={2}>
                      {mockData.suggestions.map((suggestion) => (
                        <Grid item xs={12} md={4} key={suggestion.id}>
                          <Paper
                            sx={{
                              p: 2,
                              height: '100%',
                              bgcolor: 'background.paper',
                              border: 1,
                              borderColor: 'divider',
                            }}
                          >
                            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                              <Typography variant="h6" color={suggestion.priority === 'high' ? 'error.main' : 'warning.main'}>
                                {suggestion.title}
                              </Typography>
                              <Chip 
                                label={suggestion.status} 
                                size="small" 
                                color={suggestion.status === 'Em andamento' ? 'primary' : 'default'}
                              />
                            </Box>
                            <Typography variant="body2" color="text.secondary" gutterBottom>
                              Departamento: {suggestion.department}
                            </Typography>
                            <Typography variant="body1" sx={{ mb: 2 }}>
                              {suggestion.description}
                            </Typography>
                            <Typography variant="body2" color="success.main" sx={{ mb: 1 }}>
                              Impacto: {suggestion.impact}
                            </Typography>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <Box sx={{ width: '100%', mr: 1 }}>
                                <LinearProgress 
                                  variant="determinate" 
                                  value={suggestion.progress} 
                                  color={suggestion.priority === 'high' ? 'error' : 'warning'}
                                />
                              </Box>
                              <Typography variant="body2" color="text.secondary">
                                {suggestion.progress}%
                              </Typography>
                            </Box>
                            <Typography variant="caption" color="text.secondary">
                              Prazo: {suggestion.deadline}
                            </Typography>
                          </Paper>
                        </Grid>
                      ))}
                    </Grid>
                  </Paper>
                </Grid>

                {/* ESG Metrics */}
                <Grid item xs={12} md={4}>
                  <Paper sx={{ p: 3, height: '100%' }}>
                    <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                      <AssessmentIcon sx={{ mr: 1 }} />
                      Métricas ESG
                    </Typography>
                    <Grid container spacing={2}>
                      {Object.entries(mockData.esgMetrics).map(([key, value]) => (
                        <Grid item xs={12} key={key}>
                          <Paper sx={{ p: 2 }}>
                            <Typography variant="subtitle1" gutterBottom>
                              {key.charAt(0).toUpperCase() + key.slice(1)}
                            </Typography>
                            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                              <Box sx={{ flexGrow: 1, mr: 1 }}>
                                <LinearProgress 
                                  variant="determinate" 
                                  value={(value.score / value.target) * 100}
                                  color={value.score >= value.target ? "success" : "warning"}
                                />
                              </Box>
                              <Typography variant="body2">
                                {value.score}/{value.target}
                              </Typography>
                            </Box>
                            <Chip 
                              label={value.status} 
                              size="small"
                              color={value.status === "Atingido" ? "success" : "warning"}
                            />
                          </Paper>
                        </Grid>
                      ))}
                    </Grid>
                  </Paper>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
