import React from 'react';
import { 
  Paper, 
  Typography, 
  Box, 
  LinearProgress,
  Chip
} from '@mui/material';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';

const MetricsCard = ({ 
  title, 
  value, 
  unit, 
  reduction, 
  previous, 
  impact, 
  icon: Icon, 
  color = 'primary',
  data = []
}) => {
  const isPositive = reduction > 0;
  const progress = data.length > 0 ? (data[data.length - 1] / Math.max(...data)) * 100 : 0;

  return (
    <Paper sx={{ 
      p: 2,
      height: '100%',
      position: 'relative',
      overflow: 'hidden',
      '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        height: '4px',
        background: isPositive 
          ? 'linear-gradient(90deg, #2e7d32 0%, #4caf50 100%)'
          : 'linear-gradient(90deg, #d32f2f 0%, #ef5350 100%)',
      }
    }}>
      <Box sx={{ display: 'flex', alignItems: 'flex-start', mb: 2 }}>
        <Box sx={{ 
          p: 1, 
          borderRadius: 1, 
          bgcolor: `${color}.light`,
          color: `${color}.main`,
          mr: 2
        }}>
          <Icon />
        </Box>
        <Box sx={{ flex: 1 }}>
          <Typography variant="subtitle2" color="text.secondary">
            {title}
          </Typography>
          <Typography variant="h4" sx={{ fontWeight: 600 }}>
            {value}{unit}
          </Typography>
        </Box>
      </Box>

      <Box sx={{ mb: 2 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
          <Box sx={{ flex: 1, mr: 1 }}>
            <LinearProgress 
              variant="determinate" 
              value={progress}
              color={isPositive ? "success" : "error"}
              sx={{ 
                height: 8,
                borderRadius: 4,
                bgcolor: isPositive ? 'success.light' : 'error.light',
                '& .MuiLinearProgress-bar': {
                  borderRadius: 4,
                }
              }}
            />
          </Box>
          <Typography variant="body2" color="text.secondary" sx={{ fontWeight: 600 }}>
            {progress.toFixed(0)}%
          </Typography>
        </Box>
      </Box>

      <Box sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'space-between'
      }}>
        <Box>
          <Typography variant="caption" color="text.secondary" sx={{ display: 'block' }}>
            Anterior: {previous}{unit}
          </Typography>
          <Chip
            icon={isPositive ? <TrendingUpIcon /> : <TrendingDownIcon />}
            label={`${isPositive ? '+' : ''}${reduction}%`}
            color={isPositive ? "success" : "error"}
            size="small"
            sx={{ mt: 1 }}
          />
        </Box>
        <Typography variant="caption" color="success.main" sx={{ fontWeight: 600 }}>
          {impact}
        </Typography>
      </Box>
    </Paper>
  );
};

export default MetricsCard;
