import React from 'react';
import { 
  Box, 
  Grid, 
  Paper, 
  Typography, 
  LinearProgress,
  Chip,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from '@mui/material';
import TimelineIcon from '@mui/icons-material/Timeline';
import MetricsCard from '../components/MetricsCard';

const Metrics = ({ mockData }) => {
  return (
    <Box>
      {/* Métricas Detalhadas */}
      <Grid container spacing={3}>
        {Object.entries(mockData.metrics).map(([key, value]) => (
          <Grid item xs={12} key={key}>
            <Paper sx={{ p: 3 }}>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
                <TimelineIcon sx={{ mr: 1 }} />
                {key.charAt(0).toUpperCase() + key.slice(1)}
              </Typography>
              
              <Grid container spacing={3}>
                <Grid item xs={12} md={4}>
                  <MetricsCard
                    title={key}
                    value={value.current}
                    unit={value.unit}
                    reduction={value.reduction}
                    previous={value.previous}
                    impact={value.impact}
                    icon={value.icon}
                    color={value.color}
                    data={value.data}
                  />
                </Grid>
                
                <Grid item xs={12} md={8}>
                  <TableContainer>
                    <Table>
                      <TableHead>
                        <TableRow>
                          <TableCell>Período</TableCell>
                          <TableCell align="right">Valor</TableCell>
                          <TableCell align="right">Variação</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {value.data?.map((item, index) => {
                          const previousValue = index > 0 ? value.data[index - 1].value : null;
                          const variation = previousValue 
                            ? ((item.value - previousValue) / previousValue * 100).toFixed(1)
                            : 0;
                          
                          return (
                            <TableRow key={item.name}>
                              <TableCell>{item.name}</TableCell>
                              <TableCell align="right">
                                {item.value} {value.unit}
                              </TableCell>
                              <TableCell align="right">
                                <Chip 
                                  label={`${variation > 0 ? '+' : ''}${variation}%`}
                                  color={variation < 0 ? 'success' : 'error'}
                                  size="small"
                                />
                              </TableCell>
                            </TableRow>
                          );
                        })}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </Grid>
              </Grid>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Metrics;
