import React, { useState } from 'react';
import { 
  Box, 
  Grid, 
  Paper, 
  Typography, 
  Card, 
  CardContent,
  CardActions,
  Button,
  Chip,
  LinearProgress,
  Divider,
  IconButton,
  Menu,
  MenuItem,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  ListItemSecondaryAction,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  FormControl,
  InputLabel,
  Select,
  InputAdornment,
  Tooltip,
  Avatar,
  Badge
} from '@mui/material';
import LocalOfferIcon from '@mui/icons-material/LocalOffer';
import FilterListIcon from '@mui/icons-material/FilterList';
import SortIcon from '@mui/icons-material/Sort';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import WarningIcon from '@mui/icons-material/Warning';
import InfoIcon from '@mui/icons-material/Info';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import GroupsIcon from '@mui/icons-material/Groups';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import ShareIcon from '@mui/icons-material/Share';
import DownloadIcon from '@mui/icons-material/Download';
import PrintIcon from '@mui/icons-material/Print';

const Suggestions = ({ mockData }) => {
  const [filterAnchorEl, setFilterAnchorEl] = useState(null);
  const [sortAnchorEl, setSortAnchorEl] = useState(null);
  const [selectedSuggestion, setSelectedSuggestion] = useState(null);
  const [showDetailsDialog, setShowDetailsDialog] = useState(false);
  const [showNewSuggestionDialog, setShowNewSuggestionDialog] = useState(false);
  const [filters, setFilters] = useState({
    status: 'all',
    priority: 'all',
    department: 'all',
    search: ''
  });
  const [sortBy, setSortBy] = useState('priority');

  const handleFilterClick = (event) => {
    setFilterAnchorEl(event.currentTarget);
  };

  const handleSortClick = (event) => {
    setSortAnchorEl(event.currentTarget);
  };

  const handleFilterClose = () => {
    setFilterAnchorEl(null);
  };

  const handleSortClose = () => {
    setSortAnchorEl(null);
  };

  const handleFilterChange = (filterType, value) => {
    setFilters({
      ...filters,
      [filterType]: value
    });
    handleFilterClose();
  };

  const handleSortChange = (value) => {
    setSortBy(value);
    handleSortClose();
  };

  const handleSuggestionClick = (suggestion) => {
    setSelectedSuggestion(suggestion);
    setShowDetailsDialog(true);
  };

  const handleCloseDetails = () => {
    setShowDetailsDialog(false);
  };

  const handleNewSuggestion = () => {
    setShowNewSuggestionDialog(true);
  };

  const handleCloseNewSuggestion = () => {
    setShowNewSuggestionDialog(false);
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'error';
      case 'medium':
        return 'warning';
      case 'low':
        return 'info';
      default:
        return 'default';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Em andamento':
        return 'primary';
      case 'Planejado':
        return 'warning';
      case 'Concluído':
        return 'success';
      default:
        return 'default';
    }
  };

  const getPriorityIcon = (priority) => {
    switch (priority) {
      case 'high':
        return <TrendingUpIcon />;
      case 'medium':
        return <InfoIcon />;
      case 'low':
        return <TrendingDownIcon />;
      default:
        return null;
    }
  };

  // Filtrar e ordenar sugestões
  const filteredSuggestions = mockData.suggestions.filter(suggestion => {
    if (filters.status !== 'all' && suggestion.status !== filters.status) return false;
    if (filters.priority !== 'all' && suggestion.priority !== filters.priority) return false;
    if (filters.department !== 'all' && suggestion.department !== filters.department) return false;
    if (filters.search && !suggestion.title.toLowerCase().includes(filters.search.toLowerCase())) return false;
    return true;
  }).sort((a, b) => {
    switch (sortBy) {
      case 'priority':
        const priorityOrder = { high: 0, medium: 1, low: 2 };
        return priorityOrder[a.priority] - priorityOrder[b.priority];
      case 'progress':
        return b.progress - a.progress;
      case 'deadline':
        return new Date(a.deadline) - new Date(b.deadline);
      default:
        return 0;
    }
  });

  return (
    <Box>
      {/* Cabeçalho */}
      <Paper sx={{ p: 3, mb: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
          <Typography variant="h5" sx={{ display: 'flex', alignItems: 'center' }}>
            <LocalOfferIcon sx={{ mr: 1 }} />
            Sugestões de Otimização
          </Typography>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button 
              variant="contained" 
              startIcon={<AddIcon />}
              onClick={handleNewSuggestion}
            >
              Nova Sugestão
            </Button>
            <Button 
              variant="outlined" 
              startIcon={<DownloadIcon />}
            >
              Exportar
            </Button>
            <Button 
              variant="outlined" 
              startIcon={<PrintIcon />}
            >
              Imprimir
            </Button>
          </Box>
        </Box>

        {/* Filtros e Busca */}
        <Grid container spacing={2} sx={{ mb: 3 }}>
          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              placeholder="Buscar sugestões..."
              value={filters.search}
              onChange={(e) => handleFilterChange('search', e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
          </Grid>
          <Grid item xs={12} md={6}>
            <Box sx={{ display: 'flex', gap: 2 }}>
              <Button
                variant="outlined"
                startIcon={<FilterListIcon />}
                onClick={handleFilterClick}
                sx={{ flex: 1 }}
              >
                Filtros
              </Button>
              <Button
                variant="outlined"
                startIcon={<SortIcon />}
                onClick={handleSortClick}
                sx={{ flex: 1 }}
              >
                Ordenar
              </Button>
            </Box>
          </Grid>
        </Grid>

        {/* Chips de Filtros Ativos */}
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
          {filters.status !== 'all' && (
            <Chip 
              label={`Status: ${filters.status}`} 
              onDelete={() => handleFilterChange('status', 'all')} 
              color={getStatusColor(filters.status)}
            />
          )}
          {filters.priority !== 'all' && (
            <Chip 
              label={`Prioridade: ${filters.priority}`} 
              onDelete={() => handleFilterChange('priority', 'all')} 
              color={getPriorityColor(filters.priority)}
            />
          )}
          {filters.department !== 'all' && (
            <Chip 
              label={`Departamento: ${filters.department}`} 
              onDelete={() => handleFilterChange('department', 'all')} 
            />
          )}
        </Box>
      </Paper>

      {/* Lista de Sugestões */}
      <Grid container spacing={3}>
        {filteredSuggestions.map((suggestion) => (
          <Grid item xs={12} md={6} lg={4} key={suggestion.id}>
            <Card 
              sx={{ 
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                position: 'relative',
                '&:hover': {
                  boxShadow: 6,
                }
              }}
            >
              <Box 
                sx={{ 
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  right: 0,
                  height: '4px',
                  bgcolor: suggestion.priority === 'high' ? 'error.main' : 
                           suggestion.priority === 'medium' ? 'warning.main' : 'info.main'
                }}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                  <Typography variant="h6" component="div" sx={{ fontWeight: 600 }}>
                    {suggestion.title}
                  </Typography>
                  <IconButton size="small">
                    <MoreVertIcon />
                  </IconButton>
                </Box>
                
                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                  {suggestion.description}
                </Typography>
                
                <Box sx={{ mb: 2 }}>
                  <Typography variant="body2" color="success.main" sx={{ fontWeight: 600, mb: 1 }}>
                    Impacto: {suggestion.impact}
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <Box sx={{ flex: 1, mr: 1 }}>
                      <LinearProgress 
                        variant="determinate" 
                        value={suggestion.progress} 
                        color={suggestion.priority === 'high' ? 'error' : 'warning'}
                        sx={{ 
                          height: 8,
                          borderRadius: 4,
                          bgcolor: suggestion.priority === 'high' ? 'error.light' : 'warning.light',
                          '& .MuiLinearProgress-bar': {
                            borderRadius: 4,
                          }
                        }}
                      />
                    </Box>
                    <Typography variant="body2" color="text.secondary" sx={{ fontWeight: 600 }}>
                      {suggestion.progress}%
                    </Typography>
                  </Box>
                </Box>

                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mb: 2 }}>
                  <Chip 
                    label={suggestion.department} 
                    size="small" 
                    sx={{ 
                      bgcolor: 'primary.light',
                      color: 'primary.contrastText',
                      fontWeight: 500
                    }}
                  />
                  <Chip 
                    label={suggestion.status} 
                    size="small" 
                    color={suggestion.status === "Em andamento" ? "success" : "warning"}
                    sx={{ fontWeight: 500 }}
                  />
                  <Chip 
                    icon={getPriorityIcon(suggestion.priority)}
                    label={`Prioridade ${suggestion.priority === 'high' ? 'Alta' : suggestion.priority === 'medium' ? 'Média' : 'Baixa'}`} 
                    size="small" 
                    color={getPriorityColor(suggestion.priority)}
                  />
                </Box>
              </CardContent>
              <Divider />
              <CardActions sx={{ justifyContent: 'space-between', p: 2 }}>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <CalendarTodayIcon sx={{ fontSize: 16, mr: 0.5, color: 'text.secondary' }} />
                  <Typography variant="caption" color="text.secondary">
                    Prazo: {suggestion.deadline}
                  </Typography>
                </Box>
                <Box>
                  <Button 
                    size="small" 
                    sx={{ mr: 1 }}
                    onClick={() => handleSuggestionClick(suggestion)}
                  >
                    Detalhes
                  </Button>
                  <Button 
                    variant="contained" 
                    size="small"
                    color={suggestion.priority === 'high' ? 'error' : 'warning'}
                  >
                    Implementar
                  </Button>
                </Box>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Menu de Filtros */}
      <Menu
        anchorEl={filterAnchorEl}
        open={Boolean(filterAnchorEl)}
        onClose={handleFilterClose}
      >
        <MenuItem>
          <FormControl fullWidth>
            <InputLabel>Status</InputLabel>
            <Select
              value={filters.status}
              label="Status"
              onChange={(e) => handleFilterChange('status', e.target.value)}
            >
              <MenuItem value="all">Todos</MenuItem>
              <MenuItem value="Em andamento">Em andamento</MenuItem>
              <MenuItem value="Planejado">Planejado</MenuItem>
              <MenuItem value="Concluído">Concluído</MenuItem>
            </Select>
          </FormControl>
        </MenuItem>
        <MenuItem>
          <FormControl fullWidth>
            <InputLabel>Prioridade</InputLabel>
            <Select
              value={filters.priority}
              label="Prioridade"
              onChange={(e) => handleFilterChange('priority', e.target.value)}
            >
              <MenuItem value="all">Todas</MenuItem>
              <MenuItem value="high">Alta</MenuItem>
              <MenuItem value="medium">Média</MenuItem>
              <MenuItem value="low">Baixa</MenuItem>
            </Select>
          </FormControl>
        </MenuItem>
        <MenuItem>
          <FormControl fullWidth>
            <InputLabel>Departamento</InputLabel>
            <Select
              value={filters.department}
              label="Departamento"
              onChange={(e) => handleFilterChange('department', e.target.value)}
            >
              <MenuItem value="all">Todos</MenuItem>
              <MenuItem value="TI">TI</MenuItem>
              <MenuItem value="Infraestrutura">Infraestrutura</MenuItem>
              <MenuItem value="Marketing">Marketing</MenuItem>
              <MenuItem value="RH">RH</MenuItem>
            </Select>
          </FormControl>
        </MenuItem>
      </Menu>

      {/* Menu de Ordenação */}
      <Menu
        anchorEl={sortAnchorEl}
        open={Boolean(sortAnchorEl)}
        onClose={handleSortClose}
      >
        <MenuItem onClick={() => handleSortChange('priority')}>
          Ordenar por Prioridade
        </MenuItem>
        <MenuItem onClick={() => handleSortChange('progress')}>
          Ordenar por Progresso
        </MenuItem>
        <MenuItem onClick={() => handleSortChange('deadline')}>
          Ordenar por Prazo
        </MenuItem>
      </Menu>

      {/* Dialog de Detalhes da Sugestão */}
      <Dialog
        open={showDetailsDialog}
        onClose={handleCloseDetails}
        maxWidth="md"
        fullWidth
      >
        {selectedSuggestion && (
          <>
            <DialogTitle>
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <LocalOfferIcon sx={{ mr: 1 }} />
                {selectedSuggestion.title}
              </Box>
            </DialogTitle>
            <DialogContent dividers>
              <Grid container spacing={3}>
                <Grid item xs={12} md={8}>
                  <Typography variant="h6" gutterBottom>
                    Descrição
                  </Typography>
                  <Typography variant="body1" paragraph>
                    {selectedSuggestion.description}
                  </Typography>

                  <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
                    Impacto
                  </Typography>
                  <Typography variant="body1" paragraph color="success.main" sx={{ fontWeight: 600 }}>
                    {selectedSuggestion.impact}
                  </Typography>

                  <Typography variant="h6" gutterBottom sx={{ mt: 3 }}>
                    Plano de Implementação
                  </Typography>
                  <Typography variant="body1" paragraph>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                  </Typography>
                </Grid>
                <Grid item xs={12} md={4}>
                  <Paper sx={{ p: 2, mb: 2 }}>
                    <Typography variant="subtitle1" gutterBottom>
                      Detalhes
                    </Typography>
                    <List dense>
                      <ListItem>
                        <ListItemIcon>
                          <GroupsIcon color="primary" />
                        </ListItemIcon>
                        <ListItemText 
                          primary="Departamento" 
                          secondary={selectedSuggestion.department} 
                        />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon>
                          <CalendarTodayIcon color="primary" />
                        </ListItemIcon>
                        <ListItemText 
                          primary="Prazo" 
                          secondary={selectedSuggestion.deadline} 
                        />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon>
                          {selectedSuggestion.priority === 'high' ? 
                            <TrendingUpIcon color="error" /> : 
                            <TrendingDownIcon color="info" />}
                        </ListItemIcon>
                        <ListItemText 
                          primary="Prioridade" 
                          secondary={selectedSuggestion.priority === 'high' ? 'Alta' : 
                                    selectedSuggestion.priority === 'medium' ? 'Média' : 'Baixa'} 
                        />
                      </ListItem>
                      <ListItem>
                        <ListItemIcon>
                          <CheckCircleIcon color="success" />
                        </ListItemIcon>
                        <ListItemText 
                          primary="Status" 
                          secondary={selectedSuggestion.status} 
                        />
                      </ListItem>
                    </List>
                  </Paper>

                  <Paper sx={{ p: 2 }}>
                    <Typography variant="subtitle1" gutterBottom>
                      Progresso
                    </Typography>
                    <Box sx={{ mb: 2 }}>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                        <Typography variant="body2">
                          {selectedSuggestion.progress}% Concluído
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {selectedSuggestion.progress < 100 ? 'Em andamento' : 'Concluído'}
                        </Typography>
                      </Box>
                      <LinearProgress 
                        variant="determinate" 
                        value={selectedSuggestion.progress} 
                        color={selectedSuggestion.priority === 'high' ? 'error' : 'warning'}
                        sx={{ 
                          height: 10,
                          borderRadius: 5,
                          bgcolor: selectedSuggestion.priority === 'high' ? 'error.light' : 'warning.light',
                          '& .MuiLinearProgress-bar': {
                            borderRadius: 5,
                          }
                        }}
                      />
                    </Box>
                    <Typography variant="subtitle2" gutterBottom>
                      Equipe Responsável
                    </Typography>
                    <Box sx={{ display: 'flex', mb: 1 }}>
                      <Avatar sx={{ width: 32, height: 32, mr: 1 }}>JD</Avatar>
                      <Avatar sx={{ width: 32, height: 32, mr: 1 }}>AS</Avatar>
                      <Avatar sx={{ width: 32, height: 32, mr: 1 }}>RK</Avatar>
                      <Avatar sx={{ width: 32, height: 32 }}>+2</Avatar>
                    </Box>
                  </Paper>
                </Grid>
              </Grid>
            </DialogContent>
            <DialogActions>
              <Button onClick={handleCloseDetails}>Fechar</Button>
              <Button 
                variant="outlined" 
                startIcon={<ShareIcon />}
              >
                Compartilhar
              </Button>
              <Button 
                variant="contained" 
                color={selectedSuggestion.priority === 'high' ? 'error' : 'warning'}
              >
                Implementar
              </Button>
            </DialogActions>
          </>
        )}
      </Dialog>

      {/* Dialog de Nova Sugestão */}
      <Dialog
        open={showNewSuggestionDialog}
        onClose={handleCloseNewSuggestion}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <AddIcon sx={{ mr: 1 }} />
            Nova Sugestão de Otimização
          </Box>
        </DialogTitle>
        <DialogContent dividers>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Título"
                variant="outlined"
                margin="normal"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Descrição"
                variant="outlined"
                margin="normal"
                multiline
                rows={4}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <FormControl fullWidth margin="normal">
                <InputLabel>Departamento</InputLabel>
                <Select
                  label="Departamento"
                >
                  <MenuItem value="TI">TI</MenuItem>
                  <MenuItem value="Infraestrutura">Infraestrutura</MenuItem>
                  <MenuItem value="Marketing">Marketing</MenuItem>
                  <MenuItem value="RH">RH</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} md={6}>
              <FormControl fullWidth margin="normal">
                <InputLabel>Prioridade</InputLabel>
                <Select
                  label="Prioridade"
                >
                  <MenuItem value="high">Alta</MenuItem>
                  <MenuItem value="medium">Média</MenuItem>
                  <MenuItem value="low">Baixa</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Impacto Estimado"
                variant="outlined"
                margin="normal"
                placeholder="Ex: Redução de 15% no consumo de energia"
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Prazo"
                variant="outlined"
                margin="normal"
                type="date"
                InputLabelProps={{
                  shrink: true,
                }}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseNewSuggestion}>Cancelar</Button>
          <Button variant="contained" color="primary">
            Criar Sugestão
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Suggestions;
