import React, { useState } from 'react';
import { 
  Box, 
  Grid, 
  Paper, 
  Typography, 
  LinearProgress,
  Chip,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Avatar,
  Button,
  Divider,
  IconButton,
  Tooltip
} from '@mui/material';
import TimelineIcon from '@mui/icons-material/Timeline';
import NotificationsActiveIcon from '@mui/icons-material/NotificationsActive';
import WarningIcon from '@mui/icons-material/Warning';
import InfoIcon from '@mui/icons-material/Info';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';
import GroupsIcon from '@mui/icons-material/Groups';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import MetricsCard from '../components/MetricsCard';
import { 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip as RechartsTooltip, 
  Legend, 
  ResponsiveContainer,
  LineChart,
  Line
} from 'recharts';

const Dashboard = ({ mockData }) => {
  // Dados para o gráfico de barras
  const barChartData = [
    { name: 'Jan', consumo: 1800, meta: 1600 },
    { name: 'Fev', consumo: 1750, meta: 1600 },
    { name: 'Mar', consumo: 1700, meta: 1600 },
    { name: 'Abr', consumo: 1650, meta: 1600 },
    { name: 'Mai', consumo: 1600, meta: 1600 },
    { name: 'Jun', consumo: 1500, meta: 1600 },
  ];

  // Dados para o gráfico de linha
  const lineChartData = [
    { name: 'Jan', emissões: 250, meta: 230 },
    { name: 'Fev', emissões: 240, meta: 230 },
    { name: 'Mar', emissões: 230, meta: 230 },
    { name: 'Abr', emissões: 220, meta: 230 },
    { name: 'Mai', emissões: 210, meta: 230 },
    { name: 'Jun', emissões: 200, meta: 230 },
  ];

  return (
    <Box>
      {/* Métricas Principais */}
      <Grid container spacing={3} sx={{ mb: 3 }}>
        {Object.entries(mockData.metrics).map(([key, value]) => (
          <Grid item xs={12} sm={6} md={3} key={key}>
            <MetricsCard
              title={key}
              value={value.current}
              unit={value.unit}
              reduction={value.reduction}
              previous={value.previous}
              impact={value.impact}
              icon={value.icon}
              color={value.color}
              data={value.data}
            />
          </Grid>
        ))}
      </Grid>

      {/* Gráficos e Alertas */}
      <Grid container spacing={3}>
        {/* Gráfico de Barras - Consumo de Energia */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center' }}>
                <TimelineIcon sx={{ mr: 1 }} />
                Consumo de Energia vs. Meta
              </Typography>
              <IconButton size="small">
                <MoreVertIcon />
              </IconButton>
            </Box>
            <Box sx={{ height: 300 }}>
              <ResponsiveContainer width="100%" height="100%">
                <BarChart
                  data={barChartData}
                  margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <RechartsTooltip />
                  <Legend />
                  <Bar dataKey="consumo" name="Consumo (kWh)" fill="#1B3B36" />
                  <Bar dataKey="meta" name="Meta (kWh)" fill="#4CAF50" />
                </BarChart>
              </ResponsiveContainer>
            </Box>
          </Paper>
        </Grid>

        {/* Alertas */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center' }}>
                <NotificationsActiveIcon sx={{ mr: 1 }} />
                Alertas Recentes
              </Typography>
              <Button size="small" endIcon={<ArrowForwardIcon />}>
                Ver todos
              </Button>
            </Box>
            <List>
              {mockData.alerts.map((alert, index) => (
                <ListItem key={index} sx={{ 
                  bgcolor: alert.type === 'warning' ? 'warning.light' : 'info.light',
                  mb: 1,
                  borderRadius: 1
                }}>
                  <ListItemIcon>
                    {alert.type === 'warning' ? <WarningIcon color="warning" /> : <InfoIcon color="info" />}
                  </ListItemIcon>
                  <ListItemText
                    primary={alert.message}
                    secondary={
                      <>
                        <Typography variant="body2">{alert.suggestion}</Typography>
                        <Typography variant="caption" color="text.secondary">
                          {alert.timestamp}
                        </Typography>
                      </>
                    }
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>

        {/* Gráfico de Linha - Emissões de CO2 */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center' }}>
                <TimelineIcon sx={{ mr: 1 }} />
                Emissões de CO2 ao Longo do Tempo
              </Typography>
              <IconButton size="small">
                <MoreVertIcon />
              </IconButton>
            </Box>
            <Box sx={{ height: 300 }}>
              <ResponsiveContainer width="100%" height="100%">
                <LineChart
                  data={lineChartData}
                  margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                >
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <RechartsTooltip />
                  <Legend />
                  <Line 
                    type="monotone" 
                    dataKey="emissões" 
                    name="Emissões (tCO2)" 
                    stroke="#2e7d32" 
                    activeDot={{ r: 8 }} 
                  />
                  <Line 
                    type="monotone" 
                    dataKey="meta" 
                    name="Meta (tCO2)" 
                    stroke="#ed6c02" 
                    strokeDasharray="5 5" 
                  />
                </LineChart>
              </ResponsiveContainer>
            </Box>
          </Paper>
        </Grid>

        {/* Departamentos e Conquistas */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3, height: '100%' }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center' }}>
                <GroupsIcon sx={{ mr: 1 }} />
                Departamentos em Destaque
              </Typography>
              <Button size="small" endIcon={<ArrowForwardIcon />}>
                Ver ranking
              </Button>
            </Box>
            <List>
              {mockData.gamification.topDepartments.map((dept, index) => (
                <ListItem key={index} sx={{ mb: 2 }}>
                  <ListItemIcon>
                    <Avatar sx={{ bgcolor: index === 0 ? 'warning.main' : 'primary.main' }}>
                      {index + 1}
                    </Avatar>
                  </ListItemIcon>
                  <ListItemText
                    primary={dept.name}
                    secondary={
                      <>
                        <Typography variant="body2" color="success.main" sx={{ fontWeight: 600 }}>
                          Redução: {dept.reduction}
                        </Typography>
                        <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
                          {dept.achievements.map((achievement, i) => (
                            <Tooltip key={i} title={achievement}>
                              <Chip 
                                icon={<EmojiEventsIcon />} 
                                label={achievement} 
                                size="small" 
                                color={index === 0 ? "warning" : "default"}
                              />
                            </Tooltip>
                          ))}
                        </Box>
                      </>
                    }
                  />
                </ListItem>
              ))}
            </List>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
