import React, { useState, useEffect, useRef } from 'react';
import { 
  Box, 
  TextField, 
  IconButton, 
  Paper, 
  Typography, 
  List, 
  ListItem, 
  ListItemText,
  Avatar,
  Chip,
  Fade,
  Zoom,
  CircularProgress,
  Divider,
  Tooltip
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import PersonIcon from '@mui/icons-material/Person';
import LightbulbIcon from '@mui/icons-material/Lightbulb';
import RestartAltIcon from '@mui/icons-material/RestartAlt';
import io from 'socket.io-client';

// Sugestões rápidas predefinidas
const QUICK_SUGGESTIONS = [
  "Como reduzir consumo de energia?",
  "Dicas para economizar armazenamento",
  "Como diminuir emissões de carbono?",
  "Explique o impacto do email"
];

// Mensagem de boas-vindas do bot
const WELCOME_MESSAGE = "Olá! Sou o EcoBot, seu assistente para sustentabilidade digital. Como posso ajudar você hoje?";

const ChatBot = () => {
  const [messages, setMessages] = useState([
    { text: WELCOME_MESSAGE, isBot: true }
  ]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);
  const socket = useRef();
  const inputRef = useRef(null);

  useEffect(() => {
    socket.current = io('http://localhost:5000');

    socket.current.on('chat response', (response) => {
      setIsTyping(false);
      setMessages((prev) => [...prev, { text: response, isBot: true }]);
    });

    return () => {
      socket.current.disconnect();
    };
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = (e) => {
    e?.preventDefault();
    if (input.trim()) {
      setMessages((prev) => [...prev, { text: input, isBot: false }]);
      setIsTyping(true);
      socket.current.emit('chat message', input);
      setInput('');
      
      // Foco no input após enviar mensagem
      setTimeout(() => {
        inputRef.current?.focus();
      }, 100);
    }
  };
  
  const handleQuickSuggestion = (suggestion) => {
    setInput(suggestion);
    setTimeout(() => {
      handleSend();
    }, 300);
  };
  
  const handleReset = () => {
    setMessages([{ text: WELCOME_MESSAGE, isBot: true }]);
  };

  return (
    <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Box sx={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        mb: 2 
      }}>
        <Box sx={{ display: 'flex', alignItems: 'center' }}>
          <Avatar 
            sx={{ 
              bgcolor: 'primary.main', 
              mr: 1,
              width: 32,
              height: 32
            }}
          >
            <SmartToyIcon fontSize="small" />
          </Avatar>
          <Typography variant="h6">
            EcoBot Assistant
          </Typography>
        </Box>
        <Tooltip title="Reiniciar conversa">
          <IconButton 
            onClick={handleReset}
            size="small"
            color="primary"
          >
            <RestartAltIcon />
          </IconButton>
        </Tooltip>
      </Box>
      
      <Paper
        elevation={0}
        sx={{
          flexGrow: 1,
          overflow: 'auto',
          mb: 2,
          p: 2,
          bgcolor: 'background.default',
          borderRadius: 2,
          border: '1px solid',
          borderColor: 'divider',
        }}
      >
        <List>
          {messages.map((message, index) => (
            <Fade in={true} key={index} timeout={(index + 1) * 300}>
              <ListItem
                disableGutters
                sx={{
                  justifyContent: message.isBot ? 'flex-start' : 'flex-end',
                  mb: 1.5,
                }}
              >
                {message.isBot && (
                  <Avatar 
                    sx={{ 
                      bgcolor: 'primary.main', 
                      mr: 1,
                      width: 32,
                      height: 32
                    }}
                  >
                    <SmartToyIcon fontSize="small" />
                  </Avatar>
                )}
                <Paper
                  elevation={0}
                  sx={{
                    p: 1.5,
                    maxWidth: '75%',
                    bgcolor: message.isBot ? 'primary.light' : 'secondary.light',
                    color: message.isBot ? 'primary.contrastText' : 'secondary.contrastText',
                    borderRadius: message.isBot ? '0 12px 12px 12px' : '12px 0 12px 12px',
                    wordBreak: 'break-word',
                  }}
                >
                  <Typography variant="body2">
                    {message.text}
                  </Typography>
                </Paper>
                {!message.isBot && (
                  <Avatar 
                    sx={{ 
                      bgcolor: 'secondary.main', 
                      ml: 1,
                      width: 32,
                      height: 32
                    }}
                  >
                    <PersonIcon fontSize="small" />
                  </Avatar>
                )}
              </ListItem>
            </Fade>
          ))}
          
          {isTyping && (
            <Fade in={true}>
              <ListItem
                disableGutters
                sx={{
                  justifyContent: 'flex-start',
                  mb: 1.5,
                }}
              >
                <Avatar 
                  sx={{ 
                    bgcolor: 'primary.main', 
                    mr: 1,
                    width: 32,
                    height: 32
                  }}
                >
                  <SmartToyIcon fontSize="small" />
                </Avatar>
                <Paper
                  elevation={0}
                  sx={{
                    p: 1.5,
                    bgcolor: 'primary.light',
                    color: 'primary.contrastText',
                    borderRadius: '0 12px 12px 12px',
                    minWidth: 100,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center'
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <CircularProgress size={16} color="inherit" sx={{ opacity: 0.7 }} />
                    <Typography variant="body2" sx={{ ml: 1 }}>
                      Digitando...
                    </Typography>
                  </Box>
                </Paper>
              </ListItem>
            </Fade>
          )}
          
          <div ref={messagesEndRef} />
        </List>
      </Paper>

      <Box sx={{ mb: 2 }}>
        <Typography variant="body2" sx={{ mb: 1, display: 'flex', alignItems: 'center' }}>
          <LightbulbIcon fontSize="small" sx={{ mr: 0.5, color: 'warning.main' }} />
          Sugestões rápidas:
        </Typography>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
          {QUICK_SUGGESTIONS.map((suggestion, index) => (
            <Zoom in={true} key={index} style={{ transitionDelay: `${index * 100}ms` }}>
              <Chip 
                label={suggestion} 
                onClick={() => handleQuickSuggestion(suggestion)}
                variant="outlined"
                color="primary"
                size="small"
                sx={{ 
                  borderRadius: '16px',
                  '&:hover': {
                    bgcolor: 'primary.light',
                    color: 'primary.contrastText',
                  },
                  transition: 'all 0.2s ease'
                }}
              />
            </Zoom>
          ))}
        </Box>
      </Box>

      <Divider sx={{ mb: 2 }} />

      <Box component="form" onSubmit={handleSend} sx={{ display: 'flex', gap: 1 }}>
        <TextField
          fullWidth
          size="small"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Digite sua mensagem..."
          variant="outlined"
          inputRef={inputRef}
          autoComplete="off"
          sx={{
            '& .MuiOutlinedInput-root': {
              borderRadius: '24px',
              '&.Mui-focused fieldset': {
                borderColor: 'primary.main',
                borderWidth: '2px'
              }
            }
          }}
        />
        <IconButton 
          type="submit" 
          color="primary"
          disabled={!input.trim()}
          sx={{ 
            bgcolor: 'primary.main',
            color: 'white',
            '&:hover': {
              bgcolor: 'primary.dark',
            },
            '&.Mui-disabled': {
              bgcolor: 'action.disabledBackground',
              color: 'action.disabled'
            }
          }}
        >
          <SendIcon />
        </IconButton>
      </Box>
    </Box>
  );
};

export default ChatBot;
