// Componente para proteger rotas
const ProtectedRoute = ({ children }) => {
  const { user } = useAuth();
  
  if (!user) {
    return <Navigate to="/login" />;
  }
  
  return <Layout>{children}</Layout>;
};

function App() {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AuthProvider>
        <Router>
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/" element={
              <ProtectedRoute>
                <Dashboard mockData={mockData} />
              </ProtectedRoute>
            } />
            <Route path="/metricas" element={
              <ProtectedRoute>
                <Metrics mockData={mockData} />
              </ProtectedRoute>
            } />
            <Route path="/sugestoes" element={
              <ProtectedRoute>
                <Suggestions mockData={mockData} />
              </ProtectedRoute>
            } />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </Router>
      </AuthProvider>
    </ThemeProvider>
  );
}

export default App;
