import React from 'react';
import { 
  Card, 
  CardContent, 
  Typography, 
  Box, 
  LinearProgress,
  Tooltip,
  IconButton,
  Divider
} from '@mui/material';
import TrendingDownIcon from '@mui/icons-material/TrendingDown';
import TrendingUpIcon from '@mui/icons-material/TrendingUp';
import InfoOutlinedIcon from '@mui/icons-material/InfoOutlined';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip as RechartsTooltip, ResponsiveContainer } from 'recharts';

const formatTitle = (title) => {
  return title
    .replace(/([A-Z])/g, ' $1')
    .replace(/^./, (str) => str.toUpperCase());
};

const MetricsCard = ({ title, value, unit, reduction, previous, impact, icon, color, data }) => {
  const isPositive = reduction > 0;
  const formattedTitle = formatTitle(title);

  return (
    <Card sx={{ 
      height: '100%',
      position: 'relative',
      overflow: 'visible',
      '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        height: '4px',
        background: `linear-gradient(90deg, ${color} 0%, ${color}88 100%)`,
        borderTopLeftRadius: '12px',
        borderTopRightRadius: '12px',
      }
    }}>
      <CardContent sx={{ p: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center',
            width: 48,
            height: 48,
            borderRadius: '12px',
            bgcolor: `${color}15`,
            mr: 2
          }}>
            {icon}
          </Box>
          <Box sx={{ flex: 1 }}>
            <Typography variant="h6" component="div" sx={{ fontWeight: 600 }}>
              {formattedTitle}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              {impact}
            </Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            <Tooltip title="Comparativo com período anterior">
              <InfoOutlinedIcon sx={{ color: 'text.secondary', opacity: 0.5 }} />
            </Tooltip>
            <IconButton size="small">
              <MoreVertIcon />
            </IconButton>
          </Box>
        </Box>

        <Box sx={{ display: 'flex', alignItems: 'baseline', mb: 2 }}>
          <Typography variant="h4" component="div" sx={{ 
            fontWeight: 700,
            color: 'text.primary',
            mr: 1
          }}>
            {value}
          </Typography>
          <Typography variant="body2" color="text.secondary" sx={{ fontWeight: 500 }}>
            {unit}
          </Typography>
        </Box>

        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center',
            color: isPositive ? 'success.main' : 'error.main',
            bgcolor: isPositive ? 'success.light' : 'error.light',
            py: 0.5,
            px: 1,
            borderRadius: '6px',
            mr: 2
          }}>
            {isPositive ? <TrendingDownIcon sx={{ fontSize: 16, mr: 0.5 }} /> : <TrendingUpIcon sx={{ fontSize: 16, mr: 0.5 }} />}
            <Typography variant="body2" sx={{ fontWeight: 600 }}>
              {Math.abs(reduction)}%
            </Typography>
          </Box>
          <Typography variant="body2" color="text.secondary">
            vs. anterior: {previous} {unit}
          </Typography>
        </Box>

        {data && (
          <Box sx={{ height: 100, mb: 2 }}>
            <ResponsiveContainer width="100%" height="100%">
              <AreaChart data={data}>
                <defs>
                  <linearGradient id={`gradient-${title}`} x1="0" y1="0" x2="0" y2="1">
                    <stop offset="5%" stopColor={color} stopOpacity={0.3}/>
                    <stop offset="95%" stopColor={color} stopOpacity={0}/>
                  </linearGradient>
                </defs>
                <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                <XAxis dataKey="name" stroke="#666" fontSize={12} />
                <YAxis stroke="#666" fontSize={12} />
                <RechartsTooltip 
                  contentStyle={{ 
                    background: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
                  }}
                />
                <Area 
                  type="monotone" 
                  dataKey="value" 
                  stroke={color} 
                  fill={`url(#gradient-${title})`}
                  strokeWidth={2}
                />
              </AreaChart>
            </ResponsiveContainer>
          </Box>
        )}

        <Divider sx={{ my: 2 }} />

        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Typography variant="body2" color="text.secondary">
            Meta: {Math.round(value * 0.9)} {unit}
          </Typography>
          <Box sx={{ width: '60%' }}>
            <LinearProgress 
              variant="determinate" 
              value={reduction > 0 ? 100 : 0} 
              color={reduction > 0 ? "success" : "error"}
              sx={{ 
                height: 8,
                borderRadius: 4,
                bgcolor: reduction > 0 ? 'success.light' : 'error.light',
                '& .MuiLinearProgress-bar': {
                  borderRadius: 4,
                }
              }}
            />
          </Box>
        </Box>
      </CardContent>
    </Card>
  );
};

export default MetricsCard;
